import React from 'react';

const HomePage = () => {
  return (
    <div>
      {/* About Section */}
      <section id="about" className="py-20 bg-gray-100">
        <div className="container mx-auto px-4 text-center">
          <h2 className="text-4xl font-bold mb-6">About Me</h2>
          <p className="text-xl max-w-3xl mx-auto">
            Hello, I'm <span className="font-semibold">Iuliia Obukhova</span>, a Full Stack Web Developer specializing in Three.js. 
            I have a passion for creating dynamic and visually appealing web applications.
          </p>
        </div>
      </section>

      {/* Welcome Section */}
      <section className="container mx-auto p-6 text-center">
        <div className="my-12">
          <h1 className="text-5xl font-bold mb-4">Welcome to My Portfolio</h1>
          <img
            src="/to/your/picture.png"
            alt="Iuliia Obukhova"
            className="rounded-full mx-auto w-60 h-60 mb-6 border-4 border-gray-800 shadow-lg"
          />
          <p className="text-xl max-w-3xl mx-auto mb-8">
            Explore my projects and get in touch to discuss how we can work together to create amazing web experiences.
          </p>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
